import 'package:flutter/material.dart';

class FitnessTracker extends StatelessWidget {
  final List<String> calendarEvents;
  final Map<String, double> progressData;

  const FitnessTracker({
    Key? key,
    required this.calendarEvents,
    required this.progressData,
  }) : super(key: key);

  @override
  Widget build(BuildContext context) {
    return Column(
      crossAxisAlignment: CrossAxisAlignment.start,
      children: [
        const Text(
          'Fitness Tracker',
          style: TextStyle(fontSize: 24, fontWeight: FontWeight.bold),
        ),
        const SizedBox(height: 16),
        _buildCalendarEvents(),
        const SizedBox(height: 16),
        _buildProgressData(),
      ],
    );
  }

  Widget _buildCalendarEvents() {
    return Column(
      crossAxisAlignment: CrossAxisAlignment.start,
      children: [
        const Text(
          'Upcoming Events',
          style: TextStyle(fontSize: 18, fontWeight: FontWeight.w600),
        ),
        ...calendarEvents.map((event) => Padding(
              padding: const EdgeInsets.symmetric(vertical: 4),
              child: Text(event),
            )),
      ],
    );
  }

  Widget _buildProgressData() {
    return Column(
      crossAxisAlignment: CrossAxisAlignment.start,
      children: [
        const Text(
          'Progress Data',
          style: TextStyle(fontSize: 18, fontWeight: FontWeight.w600),
        ),
        ...progressData.entries.map((entry) => Padding(
              padding: const EdgeInsets.symmetric(vertical: 4),
              child: Text('${entry.key}: ${entry.value.toStringAsFixed(1)}%'),
            )),
      ],
    );
  }
}

