import 'package:flutter/material.dart';

class FitnessTrackerPreview extends StatelessWidget {
  final List<String> calendarEvents;
  final List<double> progressData;

  const FitnessTrackerPreview({
    Key? key,
    required this.calendarEvents,
    required this.progressData,
  }) : super(key: key);

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text('Fitness Tracker Preview'),
      ),
      body: Padding(
        padding: const EdgeInsets.all(16.0),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            Text(
              'Calendar Events',
              style: Theme.of(context).textTheme.headline6,
            ),
            SizedBox(height: 8),
            Expanded(
              child: ListView.builder(
                itemCount: calendarEvents.length,
                itemBuilder: (context, index) {
                  return ListTile(
                    title: Text(calendarEvents[index]),
                  );
                },
              ),
            ),
            SizedBox(height: 16),
            Text(
              'Progress Data',
              style: Theme.of(context).textTheme.headline6,
            ),
            SizedBox(height: 8),
            Expanded(
              child: ListView.builder(
                itemCount: progressData.length,
                itemBuilder: (context, index) {
                  return ListTile(
                    title: Text('Progress ${index + 1}: ${progressData[index]}%'),
                  );
                },
              ),
            ),
          ],
        ),
      ),
    );
  }
}
